import React, {Component} from 'react';
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
// import PropTypes from "prop-types";
// import { STATUS } from "../../constant";
import { contactFormAction } from "../../Actions/form.action";
import './GetInTouch.scss';
// import { getLocalstorage } from "../../helper";
// import { get } from "lodash";
import request from "../../Services/requestService";

class GetInTouch extends Component {
    constructor(props){
        super(props);
        this.state={
            name: "",
            phone: "",
            email: "",
            msg: "",
            dataChanged: false
        };
    }

    handleInputBox = (event)=> {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    }

    sendContactData = () => {
        const {name, phone, email, msg, dataChanged} = this.state;
        console.log('Clicked')
        const data = {
            "name": [{"value": name}],
            "phone": [{"value": phone}],
            "email": [{"value": email}],
            "msg": [{"value": msg}]
        }
        dataChanged === true && request(contactFormAction(data))
        .then(resp => {
            if(resp.status === 201){
                this.setState({
                    dataChanged: false
                })
            }
        })  
        .catch(e => {
            console.log('error', e);
        })
    }

    render() {
        return (
            <div className="get-in-touch wrapper">
                <span className="border git-border"></span>
                <h1>GET IN TOUCH</h1>
                <div className="git-wrapper">
                    <form>
                        <div className="name-phone">
                            <div>
                                <label>Name</label>
                                <input type="text" name="name" onChange={(e)=>this.handleInputBox(e)}/>
                            </div>
                            <div>
                                <label>Phone</label>
                                <input type="tel" name="phone" onChange={(e)=>this.handleInputBox(e)}/>
                            </div>
                        </div>
                        <div>
                            <label>Email</label>
                            <input type="email" name="email" onChange={(e)=>this.handleInputBox(e)}/>
                        </div>
                        <div>
                            <label>Write Message</label>
                            <textarea onChange={(e)=>this.handleInputBox(e)}/> 
                        </div>
                        <button type="button" onClick={this.sendContactData}>Send Message</button>
                    </form>
                    <div className="contact-details">
                        <h2>Contact Details</h2>
                        <div className="detail">
                            <p>34 Street Name, City Name Here,<br/>
                                United States
                            </p>
                            <p>+1 242 4942 290</p>
                            <p>info@gourdomain.com</p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return{
        formData: state.formReducer.data
    }
};

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
            contactFormAction: contactFormAction,
        }, dispatch
    );
};

export default connect(mapStateToProps, mapDispatchToProps)(GetInTouch);
