import React, {Component, createRef} from 'react';
import Slider from "react-slick";
import 'slick-carousel/slick/slick-theme.css';
import 'slick-carousel/slick/slick.css';
import './FeaturedClasses.scss';

class FeaturedClasses extends Component {
    constructor(props) {
        super(props);
        this.feature = createRef();
        this.state = {
            images: ['doyoga_img_1.jpg', 'doyoga_about_2.jpg', 'doyoga_img_3.jpg'],
        }
    }

    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        const { images } = this.state;
        return (
            <div className="featured-class">
                <Slider {...settings}>
                    {
                        images.map(item => (
                            <div>
                                <img src={`./assets/${item}`} alt="Do Yoga" height="100"/>
                            </div>
                        ))
                    }
                </Slider>
                <div ref={this.feature}>
                    <button className="button" onClick={this.previous}>
                        Previous
                    </button>
                    <button className="button" onClick={this.next}>
                        Next
                    </button>
                </div>
            </div>
        );
    }
}

export default FeaturedClasses;
