import * as apiServices from "../Services/apiServices";
import request from "../Services/requestService";
import { setLocalstorage } from "../helper";
import { FORM_ACTION } from "../constant";
import { get } from "lodash";

export const contactFormAction = (requestMethod = request, formInfo) => {
  const { username, phone, email, message } = formInfo;
  const payload = {
    username,
    phone,
    email,
    message
  };
  return function(dispatch) {
    dispatch({
      type: FORM_ACTION.FORM_START
    });
    return requestMethod(apiServices.contactForm(payload))
      .then(formResp => {
        const payload = formResp.data;
        dispatch({
          type: FORM_ACTION.FORM_SUCCESS,
          payload
        });
      })
      .catch(() => {
        dispatch({
          type: FORM_ACTION.FORM_FAILURE
        });
      });
  };
};
