import * as apiServices from "../Services/apiServices";
import request from "../Services/requestService";
// import { setLocalstorage } from "../helper";
import { FORM_ACTION } from "../constant";
// import { get } from "lodash";

export const contactFormAction = (requestMethod = request, payload) => {
  console.log('Actinnnnnnn called')
  return function(dispatch) {
    dispatch({
      type: FORM_ACTION.FORM_START
    });
    return requestMethod(apiServices.contactForm(payload))
      .then(formResp => {
        const payload = formResp.data;
        console.log('Actionnnnn response')
        dispatch({
          type: FORM_ACTION.FORM_SUCCESS,
          payload
        });
      })
      .catch(() => {
        dispatch({
          type: FORM_ACTION.FORM_FAILURE
        });
      });
  };
};

export const sendVerificationOTP = (requestMethod = request, params) => {
  return requestMethod(apiServices.sendVerificationOTPParams(params))
    .then(response => {
      return response;
    })
    .catch(e => {
      console.log("Reset Password Failed", e.message);
    });
};
