import React, {Component, createRef, Fragment} from 'react';
import Slider from "react-slick";
import 'slick-carousel/slick/slick-theme.css';
import 'slick-carousel/slick/slick.css';
import './FeaturedClasses.scss';

class FeaturedClasses extends Component {
    constructor(props) {
        super(props);
        this.feature = createRef();
        this.state = {
            images: ['doyoga_img_1.jpg', 'doyoga_about_2.jpg', 'doyoga_img_3.jpg'],
        }
    }

    previous = () => {
        console.log('FFF',this.feature.current)
        this.feature.current.slickPrev();
    }

    next = () => {
        this.feature.current.slickNext();
    }

    render() {
        const settings = {
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        const { images } = this.state;
        return (
            <div className="featured-wrapper">
                <div className="featured-content wrapper">
                    <div className="featured-head">
                        <span className="border"></span>
                        <h1>FEATURED CLASSES</h1>
                        <p></p>
                    </div>
                    <div className="slick-butttons">
                        <button className="button" onClick={this.previous}>
                            Previous
                        </button>
                        <button className="button" onClick={this.next}>
                            Next
                        </button>
                    </div>
                </div>
                <div className="featured-slider">
                    <Slider {...settings} ref={this.feature}>
                        {
                            images.map(item => (
                                <div>
                                    <img src={`./assets/${item}`} alt="Do Yoga" height="100"/>
                                </div>
                            ))
                        }
                    </Slider>
                </div>
            </div>
        );
    }
}

export default FeaturedClasses;
