import React, {Component} from 'react';
import Slider from "react-slick";

class FeaturedClasses extends Component {
    state = {
        images: ['doyoga_img_1.jpg', 'doyoga_about_2.jpg', 'doyoga_img_3.jpg'],
    }

    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        const { images } = this.state;
        return (
            <div className="featured-class">
                <Slider {...settings}>
                    <div className="featured-img">
                        {
                            images.map(item=> (
                                <img src={`./assets/${item}`} alt="Do Yoga"/>
                            ))
                        }
                    </div>
                </Slider>
            </div>
        );
    }
}

export default FeaturedClasses;
